import { Component, OnInit } from '@angular/core';
import { Observable, Subject } from 'rxjs';
import { Student } from './student';
import { StudentService } from '../student.service';
import {
  debounceTime, distinctUntilChanged, switchMap
} from 'rxjs/operators';

@Component({
  selector: 'app-search-student',
  templateUrl: './search-student.component.html',
  styleUrls: ['./search-student.component.css']
})

export class SearchStudentComponent implements OnInit{
  foundUsers$!: Observable<Student[]>;
  private searchTerms = new Subject<string>();

  constructor(private studentServ: StudentService){}

  search(term: string): void {
    this.searchTerms.next(term);
  }

  ngOnInit(): void {
    this.foundUsers$ = this.searchTerms.pipe(
      // wait 300ms after each keystroke before considering the term
      debounceTime(300),

      // ignore new term if same as previous term
      distinctUntilChanged(),

      // switch to new search observable each time the term changes
      switchMap((term: string) => this.studentServ.searchHeroes(term)),
    );
  }

}

<div  class="search-div-holder">
    
    <div class="action-holder">
        <button type="button" routerLink="/add-student">+</button>
        
        <div class="search-div">
            <input type="search" #searchBox id="search-box" (input)="search(searchBox.value)" placeholder="search...">
            <ul *>
                <li *ngFor="let students of foundUsers$ | async" routerLink="/student-edit/{{students.studentNumber}}">
                    {{students.firstName + " " + students.lastName}}
                </li>
            </ul>
        </div>
    </div>
</div>
