import { Component, OnInit } from '@angular/core';
import { StudentService } from '../student.service';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-student-edit',
  templateUrl: './student-edit.component.html',
  styleUrls: ['./student-edit.component.css']
})
export class StudentEditComponent implements OnInit {
student?: any;
  constructor(private serviceStudents : StudentService, private route : ActivatedRoute){}
  
  editable = true;
  
  ngOnInit(): void {
    this.getOne();
  }

  isEditable(){

      this.editable = false
 
  }

  studentNumber= ""
    firstName=""
    lastName=""
    email=""
    phoneNumber=""
    address=""
    dateOfBirth=""
    course=""
    
    studentDetails = {
      studentNumber:this.studentNumber,
      firstName: this.firstName,
      lastName:this.lastName,
      email:this.email,
      phoneNumber: this.phoneNumber,
      address: this.address,
      course: this.course,
      dateOfBirth:this.dateOfBirth
    }

  mClear(){
    this.studentDetails = {
      studentNumber:"",
      firstName: "",
      lastName:"",
      email:"",
      phoneNumber: "",
      address: "",
      course: "",
      dateOfBirth:""
    }
  }

  StuEmpty = false;
  couEmpty = false;
  addEmpty = false;
  cellEmpty = false;
  emaEmpty = false;
  dobEmpty = false;
  lnamEmpty = false;
  namEmpty = false;


mCheckValues(): boolean{
  this.StuEmpty = false;
  this.couEmpty = false;
  this.addEmpty = false;
  this.cellEmpty = false;
  this.emaEmpty = false;
  this.dobEmpty = false;
  this.lnamEmpty = false;
  this.namEmpty = false;
  
if(!this.studentDetails.studentNumber){
  this.StuEmpty=true;
  return false}
else if(!this.studentDetails.firstName){this.namEmpty = true;return false}
else if(!this.studentDetails.lastName){this.lnamEmpty = true; return false}
else if(!this.studentDetails. dateOfBirth){this.dobEmpty = true;return false}
else if(!this.studentDetails.email){this.emaEmpty = true;return false}
else if(!this.studentDetails.phoneNumber){this.cellEmpty = true;return false}
else if(!this.studentDetails.address){this.addEmpty = true;return false}
else if(!this.studentDetails.course){this.couEmpty = true;return false}

else{
return true;
}
}

show = false;

onSave() {
  this.show = false;
  if(this.mCheckValues()) {
    this.serviceStudents.updateStudents(this.studentDetails, this.studentDetails.studentNumber).subscribe({
        next: (data)=> {
          console.log(data);
          this.editable = true;
          this.show = true;

        },
        error: (e)=>{console.log("stude", e)}
      })
  }
}

  getOne(){
    const id = this.route.snapshot.params[('id')]
    this.serviceStudents.getStudent(id).subscribe({
      next: (data)=>{
        this.student = data
        this.studentDetails = this.student[0]
        console.log(data)
      },
      error: (e)=> console.log(e)
    })
  }

  deleteOne(){
    this.serviceStudents.deleteOne(this.studentDetails.studentNumber).subscribe(data => {
      alert('Success');
  });
}

}
